INSERT INTO `generos` (`id`, `nome`, `descricao`) VALUES
	(1, 'Ação', 'É um gênero de filme protagonizado por um herói de ação e que apresenta predominantemente sequências de perseguição, lutas, tiroteios, explosões e acrobacias de dublês.'),
	(2, 'Aventura', 'É um gênero cinematográfico que pode ser caracterizado como uma história onde um herói enfrenta uma série de obstáculos, exibindo coragem ao enfrentar situações que fogem ao cotidiano.'),
	(3, 'Comédia', 'É um gênero de ficção que consiste em discursos ou obras que pretendem ser humorísticos ou divertidos por induzir o riso, especialmente no teatro, cinema, comédia stand-up, televisão, rádio, livros ou qualquer outro meio de entretenimento.'),
	(6, 'Comédia Romântica', 'É um subgênero cinematográfico dos gêneros comédia e romance.'),
	(7, 'Documentário', 'É uma produção artística, via de regra um filme, não-ficcional, que se caracteriza principalmente pelo compromisso da exploração da realidade.'),
	(8, 'Drama', 'É o modo específico de ficção representado na performance: uma peça, ópera, mímica, balé, etc., realizada em um teatro, rádio ou televisão.'),
	(9, 'Espionagem', 'É um gênero de filme que trata do tema da espionagem ficcional, seja de forma realista (como as adaptações de John le Carré) ou como base para a fantasia (como muitos filmes de James Bond).'),
	(10, 'Faroeste', 'O chamado cinema western, também popularizado sob os termos "filmes de cowboys" ou "filmes de faroeste", compõe um gênero clássico do cinema norte-americano (ainda que outros países tenham produzido westerns, como aconteceu na Itália, com o seu western spaghetti).'),
	(11, 'Fantasia', 'É um gênero da ficção em que geralmente se utilizam fenômenos sobrenaturais, mágicos e outros como elementos primários do enredo, tema ou configuração.'),
	(12, 'Ficção Científica', 'É um gênero de filme que utiliza a ficção científica e a ficção especulativa com base científica de fenômenos que não são totalmente aceitos pela ciência moderna, como formas de vida extraterrestre, outros planetas, percepção extra-sensorial e viagem no tempo, juntamente com elementos futuristas tais como naves espaciais, robôs, ciborgues, rebelião das máquinas, viagens interestelares ou outras tecnologias.'),
	(13, 'Guerra', 'É um gênero cinematográfico que de um lado se confunde com filmes de ação e épicos, principalmente quando são mostrados dramas históricos, enquanto por outro tem se prestado, ao longo da história, ao uso propagandístico ou patriótico dos governos de todos os países que, em maior ou menor grau, buscam obter dividendos através de obras que retratam conflitos pontuais ou do passado.'),
	(14, 'Policial', 'É um gênero que quase sempre envolvem crimes e criminosos, policiais e detetives particulares, mafiosos e ladrões.'),
	(15, 'Mistério', 'É um gênero cinematográfico que gira em torno da solução de um problema ou de um crime.'),
	(16, 'Musical', 'É um estilo de filme, no qual a narrativa se apoia sobre uma sequência de músicas coreografadas, utilizando música, canções e coreografia como narrativa predominante ou exclusivamente.'),
	(17, 'Romance', 'Podem ser definidos como aqueles cujo enredo se desenvolve em torno de um envolvimento amoroso entre os protagonistas. Um dos pré-requisitos do gênero é de que o filme tenha um "final feliz"; contudo, alguns filmes com final triste também podem ser considerados filmes do gênero romântico.'),
	(18, 'Terror', 'É um gênero cinematográfico que procura uma reação emocional negativa dos espectadores, ao jogar com os medos primários da audiência.'),
	(19, 'Thriller', 'É um gênero da literatura, filmes, jogos eletrônicos e televisão que usa o suspense, tensão e excitação como principais elementos do gênero.');
